function frankenSplice(arr1, arr2, n) {
  // It's alive. It's alive!
   //copy the first array
  let newArr = arr2.slice(0, arr2.length);
  console.log(newArr);
  //insert the first array at the index of the second array
  newArr.splice(n, 0, ...arr1);
  console.log(newArr);
  return newArr;
}
