'use strict';
Array.prototype.last = function(){
  return this[this.length-1];
}
Array.prototype.penultimate = function() {
  return this[this.length-2];
}

function sumFibs(num) {
  let sequence = [1,1];

  while(sequence.penultimate() + sequence.last() <= num){
    sequence.push(sequence.penultimate() + sequence.last());
  }
  return sequence.filter(function(num){
    return num % 2 !== 0;
  }).reduce(function(a,b){
    return a + b;
  });
}


sumFibs(4);
